from functools import cache


class Solution:
    # Memory Limit Exceeded
    def numberOfWays1(self, s: str) -> int:
        slen = len(s)

        @cache
        def select(first, i, n):
            if n == 0:
                return 1

            if i >= slen:
                return 0

            if s[i] == first:
                return select('0' if first == '1' else '1', i + 1, n - 1) + select(first, i + 1, n)
            else:
                return select(first, i + 1, n)

        return select('0', 0, 3) + select('1', 0, 3)

    # my solution
    # Memory O(n) Time O(n)
    def numberOfWays2(self, s: str) -> int:
        n = len(s)
        zeros_after = [0] * n
        ones_after = [0] * n
        zeroones_after = [0] * n
        onezeros_after = [0] * n
        ways = 0

        zeros, ones, zeroones, onezeros = 0, 0, 0, 0
        for i in range(n - 1, -1, -1):
            zeros_after[i] = zeros
            ones_after[i] = ones
            zeroones_after[i] = zeroones
            onezeros_after[i] = onezeros
            if s[i] == '0':
                ways += onezeros_after[i]
                zeroones += ones_after[i]
                zeros += 1
            else:
                ways += zeroones_after[i]
                onezeros += zeros_after[i]
                ones += 1

        return ways

    # Memory O(1) Time O(n)
    def numberOfWays(self, s: str) -> int:
        ones_after, zeros_after, answer = 0, 0, 0

        for i in range(len(s)):
            if s[i] == '1':
                ones_after += 1
            else:
                zeros_after += 1

        ones_before, zeros_before = 0, 0
        for i in range(len(s)):
            if s[i] == '1':
                answer += zeros_before * zeros_after
                ones_after -= 1
                ones_before += 1
            else:
                answer += ones_before * ones_after
                zeros_after -= 1
                zeros_before += 1

        return answer


solution = Solution()
assert solution.numberOfWays("10001") == 3
assert solution.numberOfWays("001101") == 6
assert solution.numberOfWays("11100") == 0
assert solution.numberOfWays("") == 10719654494
